const buyButtons=document.querySelectorAll(".button-buy"),cart=document.querySelector(".cart-popup-container"),cartCloseButton=cart.querySelector(".cart-close-button");for(let t of buyButtons)t.addEventListener("click",function(t){t.preventDefault(),cart.classList.add("popup-show")});window.addEventListener("keydown",function(t){"Escape"===t.key&&cart.classList.contains("popup-show")&&(t.preventDefault(),cart.classList.remove("popup-show"))}),cartCloseButton.addEventListener("click",function(t){t.preventDefault(),cart.classList.remove("popup-show")});